try:
    from PIL import Image
except:

    try:
        import Image
    except:
        try:
            from pil import Image
        except:
            raise ModuleNotFoundError("Difference module can't find pillow module.")

def find(im1, im2, color=(255, 255, 255)):

    if im1.size != im2.size:
        raise Exception('Images have to te same size! Try: PIL.Image.open().resize()')
    x, y = im1.size
    im1 = im1.convert('RGB')
    im2 = im2.convert('RGB')
    im = Image.new(size=(x, y), mode='RGB')
    im1data = list(im1.getdata())
    im2data = list(im2.getdata())
    newlist = []
    for cnt in range(x*y):
        if tuple(im1data[cnt]) == tuple(im2data[cnt]):
            newlist.append(im1data[cnt])
        else:
            newlist.append(color)
    im.putdata(newlist)
    return im
