try:
    from PIL import Image
except:

    try:
        import Image
    except:
        try:
            from pil import Image
        except:
            raise ModuleNotFoundError("Difference module can't find pillow module.")

class create:
    def __init__(self, im):
        self.im = im
        self.x, self.y = im.size
    def first(self):
        return self.im.crop((0, 0, int(self.x/2), self.y))
    def second(self):
        return self.im.crop((int(self.x/2), 0, self.x, self.y))
